import React, { Component } from "react";
import AuthenticationService from "../service/AuthenticationService";


class LoginComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            username: '',
            password: '',
            role: '',
            firstAuth: false,
            secondAuth: false
        }

        this.loginClicked = this.loginClicked.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    render() {
        return (
            <div className="container">
                <form className="form-signin">
                    <h2>SignIn</h2>
                    <label htmlFor="username" className="sr-only">Username</label>
                    <input type="text" className="form-control" id="username" name="username" placeholder="Username" value={this.state.username} onChange={this.handleChange} required></input>

                    <label htmlFor="password" className="sr-only">Password</label>
                    <input type="password" className="form-control" id="password" name="password" placeholder="Password" value={this.state.password} onChange={this.handleChange} required></input>

                    <div className="input-group input-group-sm mb-3">
                        <div className="input-group-prepend">
                            <label className="input-group-text" htmlFor="inputGroupSelect01">Role</label>
                        </div>
                        <select className="custom-select" id="inputGroupSelect01" name="role" value={this.state.role} onChange={this.handleChange} required>
                            {/* <option selected>Choose your role</option> */}
                            <option value="reviewer">Reviewer</option>
                            <option value="admin">Admin</option>
                            <option value="superadmin">SuperAdmin</option>
                        </select>
                    </div>
                    <div>
                        <button className="btn btn-success" onClick={this.loginClicked}>Sign in</button>
                    </div>
                </form>

                <div>
                    <securityQuestion isFistAuth={this.state.firstAuth} />
                </div>
            </div>

        );

    }

    handleChange(event) {
        //console.log(this.state);
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        )
    }

    loginClicked() {
        let response = AuthenticationService.AuthenticateUser(this.state.username, this.state.password, this.state.role)
        let navigate = this.props.navigate;
        if (response) {
            this.setState = ({
                firstAuth: true
            })
            console.log(this.state);
        }

        else navigate('/')

    }


}

function securityQuestion(props) {
    if (props.isFistAuth) {
        return (
            <div>

                <label >Name your favourite Animal</label>
                <input type="text" placeholder="Please answer here" className="form-control" />

            </div>
        );
    }

    else {
        return (
            <div>error</div>
            );
    }

    

}

export default LoginComponent
