import React, { Component } from "react";


class LoginComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            username: '',
            password: '',
            role: '',
            hasLoginSuccess: false
        }

        this.loginClicked = this.loginClicked.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    render() {
        return (
            <div className="container">
                <form className="form-signin">
                    <h2>SignIn</h2>
                    <label htmlFor="username" className="sr-only">Username</label>
                    <input type="text" className="form-control" id="username" placeholder="Username" value={this.state.username} onChange={this.handleChange} required></input>

                    <label htmlFor="password" className="sr-only">Password</label>
                    <input type="password" className="form-control" id="password" placeholder="Password" value={this.state.password} onChange={this.handleChange} required></input>

                    <div className="input-group input-group-sm mb-3">
                        <div className="input-group-prepend">
                            <label className="input-group-text" htmlFor="inputGroupSelect01">Role</label>
                        </div>
                        <select className="custom-select" id="inputGroupSelect01" value={this.state.role} onChange={this.handleChange} required>
                            {/* <option selected>Choose your role</option> */}
                            <option value="1">Reviewer</option>
                            <option value="2">Admin</option>
                            <option value="3">SuperAdmin</option>
                        </select>
                    </div>
                    <div>
                        <button className="btn btn-success" onClick={this.loginClicked}>Sign in</button>
                    </div>
                </form>
            </div>

        );

    }

    handleChange(event) {
        //console.log(this.state);
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        )
    }

    loginClicked() {

        console.log(this.state);

    }


}

export default LoginComponent
