import { ErrorMessage, Field, Form, Formik } from "formik";
import React, { Component } from "react";
import AuthenticationService from "../service/AuthenticationService";
import BlogDataService from "../service/BlogDataService";
import moment from "moment";

class BlogComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.params.id,
            username: '',
            description: '',
            date: moment(new Date()).format('YYYY-MM-DD')
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)

    }

    componentDidMount() {
        if (this.state.id === -1) {
            return
        }

        BlogDataService.retrieveBlogById(this.state.id)
            .then(res => {
                this.setState({
                    description: res.data.description,
                    date: moment(res.data.date).format("MMM Do YY")
                })

            })

    }


    render() {
        let { description, date } = this.state;
        return (
            <div>
                <h1>Blog</h1>
                <div className="container">
                    <Formik
                        initialValues={{ description, date }}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="description" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="targetDate" component="div"
                                        className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Description</label>
                                        <Field className="form-control" type="text" name="description" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Target Date</label>
                                        <Field className="form-control" type="date" name="targetDate" />
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Save</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>


        )

    }


}

export default BlogComponent;
