import React, { Component } from "react";
import BlogDataService from "../service/BlogDataService";

class ListBlogComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            blogs: [],
            message : null
        }

        this.refreshTodo = this.refreshTodo.bind(this)

    }

    componentDidMount() {
        this.refreshTodo();
    }

    refreshTodo() {
        //let username = AuthenticationService.getLoggedInUserName();
        BlogDataService.retrieveAllBlogs()
            .then(res => {
                this.setState({
                    blogs: res.data
                })
            })
    }

    render() {


        return (
            <div className="container">
                <h1>List todos</h1>
                {this.state.message && <div className="alert alert-success">{this.state.message}</div>}
                <table className="table table-sm">
                    <thead className="thead-light">
                        <tr>
                            <th>Description</th>
                            <th>Is completed</th>
                            <th>Target date</th>
                            <th>Update</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.todos.map(
                                todo =>
                                    <tr key={todo.id}>
                                        <td>{todo.description}</td>
                                        <td>{todo.done.toString()}</td>
                                        <td>{moment(todo.targetDate).format('DD-MM-YYYY')}</td>
                                        <td><button className="btn btn-success" onClick={() => this.updateTodoOnClicked(todo.id)}>Update</button></td>
                                        <td><button className="btn btn-warning" onClick={() => this.deleteTodoOnClicked(todo.id)}>Delete</button></td>
                                    </tr>
                            )

                        }
                    </tbody>

                </table>
                <div>
                    <button className="btn btn-success" onClick={this.createTodoOnClicked}>Add</button>
                </div>

            </div>

        )
    }



}

export default ListBlogComponent;
