import React, { Component } from "react";
import BlogDataService from "../service/BlogDataService";
import moment from "moment";
import AuthenticationService from "../service/AuthenticationService";
const USER = 'USER';
const ADMIN = 'ADMIN';
const SUPERADMIN = 'SUPERADMIN';
const APPROVED = 'APPROVED';
const PENDING = 'PENDING';


class ListBlogComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            blogs: [],
            message: null,
            role: USER
        }

        this.refreshTodo = this.refreshTodo.bind(this)

    }

    componentDidMount() {
        this.refreshTodo();
    }

    refreshTodo() {
        let role = AuthenticationService.getUserRole();
        this.setState({ role: role });
        BlogDataService.retrieveAllBlogs()
            .then(res => {
                this.setState({
                    blogs: res.data
                })
            })

    }

    render() {


        return (
            <div className="container">
                <h1>Blog page </h1>
                {this.state.message && <div className="alert alert-success">{this.state.message}</div>}
                <table className="table table-sm">
                    <thead className="thead-light">
                        <tr>
                            <th>Description</th>

                            <th>Date</th>
                            {/* <th>Update</th>
                            <th>Delete</th> */}
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.blogs.map(
                                blog =>
                                    <tr key={blog.id} >
                                        {(blog.postStatus === APPROVED || (blog.postStatus === PENDING && this.state.role === SUPERADMIN)) && <td>{blog.description}</td>}
                                        {(blog.postStatus === APPROVED || (blog.postStatus === PENDING && this.state.role === SUPERADMIN)) && <td>{moment(blog.date).format('DD-MM-YYYY')}</td>}
                                        <td>
                                            {(blog.postStatus === PENDING && this.state.role === SUPERADMIN) && <button className="btn btn-sm btn-success" >Approve</button>}
                                            {(blog.postStatus === PENDING && this.state.role === SUPERADMIN) && <button className="btn btn-sm btn-warning" >Delete</button>}
                                        </td>

                                    </tr>

                            )

                        }
                    </tbody>

                </table>
                {(this.state.role === ADMIN || this.state.role === SUPERADMIN) && <div>
                    <button className="btn btn-sm btn-success" onClick={this.createTodoOnClicked}>create</button>
                </div>}

            </div>

        )
    }



}

export default ListBlogComponent;
